            require(tokenContracts[i] != address(0), "Invalid token contract address");
            require(amounts[i] > 0, "Amount must be greater than 0");

            IERC20 token = IERC20(tokenContracts[i]);
            token.safeTransferFrom(msg.sender, to, amounts[i]);
        }
    }

